	function validate() { 
	  if (validateEmail(email)) {	
				g_form.showFieldMsg('adresse_mail',"Adresse mail valide SFR",'info');
	  } 
	  else {
		g_form.setValue("adresse_mail",null);
		  g_form.showFieldMsg('adresse_mail','Adresse mail non valide','error');

	  }
	  return false;
	}

	validate();



	/////////////////////////////////////////////////////////////////////
}
